# module: emplexer.py
#
# This module contains the lexing rules for the EMP.
#
# Contributor: Youngdae Kim (02.17.2019)

import ply.lex as lex

tokens = [
    'COMMA',
    'ID',
    'NUMBER',
    'LPAREN',
    'RPAREN',
    'SQUOTE',
    'DQUOTE',
    'DOLLAR',
]

reserved = {
    'dualvar'     : 'DUALVAR',
    'equilibrium' : 'EQUILIBRIUM',
    'implicit'    : 'IMPLICIT',
    'max'         : 'MAX',
    'min'         : 'MIN',
    'not'         : 'NOT',
    'qvi'         : 'QVI',
    's.t.'        : 'ST',
    'vi'          : 'VI',
    'visol'       : 'VISOL'
}

tokens += list(reserved.values())

t_COMMA  = r'\,'
t_NUMBER = r'\d+'
t_LPAREN = r'\('
t_RPAREN = r'\)'
t_SQUOTE = r'\''
t_DQUOTE = r'\"'
t_DOLLAR = r'\$'

# s.t. should com first as the OR operator | matches from left to right.
def t_ID(t):
    r's\.t\. | [a-zA-Z][a-zA-Z_0-9]*'
    t.type = reserved.get(t.value.lower(),'ID') # Check for reserved words.
    return t

def t_COMMENT(t):
    r'\*.*'
    pass

def t_newline(t):
    r'\n+'
    t.lexer.lineno += len(t.value)

def t_error(t):
    print("\n\n    Illegal character '%s'" % t.value[0])
    t.lexer.skip(1)

t_ignore = ' \t'

# Build the lexer.
lexer = lex.lex()
